func NewUniswapContractGroupFilterer(cl ghost.Client) (*ContractGroupFilterer, error) {
	var err error
	o := &ContractGroupFilterer{cl: cl}
	o.abi, err = abi.JSON(strings.NewReader(UniswapV2PairMetaData.ABI))
	return o, err
}

func (c *ContractGroupFilterer) FilterLogs(opts *bind.FilterOpts, name string, addrs []common.Address, query ...[]interface{}) ([]types.Log, error) {
	// Don't crash on a lazy user
	if opts == nil {
		opts = &bind.FilterOpts{}
	}
	// Append the event selector to the query parameters and construct the topic set
	query = append([][]interface{}{{c.abi.Events[name].ID}}, query...)

	topics, err := abi.MakeTopics(query...)
	if err != nil {
		return nil, err
	}

	config := ethereum.FilterQuery{
		Addresses: addrs,
		Topics:    topics,
		FromBlock: new(big.Int).SetUint64(opts.Start),
	}
	if opts.End != nil {
		config.ToBlock = new(big.Int).SetUint64(*opts.End)
	}
	buff, err := c.cl.FilterLogs(opts.Context, config)
	return buff, err
}

func (c *ContractGroupFilterer) UnpackLog(out interface{}, event string, log types.Log) error {
	if log.Topics[0] != c.abi.Events[event].ID {
		return fmt.Errorf("event signature mismatch")
	}
	if len(log.Data) > 0 {
		if err := c.abi.UnpackIntoInterface(out, event, log.Data); err != nil {
			return err
		}
	}
	var indexed abi.Arguments
	for _, arg := range c.abi.Events[event].Inputs {
		if arg.Indexed {
			indexed = append(indexed, arg)
		}
	}
	return abi.ParseTopics(out, indexed, log.Topics[1:])
}

type SyncRaw struct {
	Reserve0 *big.Int
	Reserve1 *big.Int
	Raw      types.Log
}

func (c *ContractGroupFilterer) FilterSync(opts *bind.FilterOpts, addrs []common.Address) ([]*Sync, error) {
	logs, err := c.FilterLogs(opts, "Sync", addrs)
	if err != nil {
		return nil, err
	}
	out := make([]*Sync, 0)
	for _, log := range logs {
		evt := &Sync{
			Raw: &EventMetaData{
				Block:            log.BlockNumber,
				Transaction:      log.TxHash.Hex(),
				TransactionIndex: log.TxIndex,
				Address:          log.Address.Hex(),
				LogIndex:         log.Index,
			},
		}
		raw := new(SyncRaw)
		if err := c.UnpackLog(raw, "Sync", log); err != nil {
			return nil, err
		}
		evt.Reserve0 = raw.Reserve0
		evt.Reserve1 = raw.Reserve1
		out = append(out, evt)
	}
	return out, nil
}
